mapper.select:
SELECT COUNT(*) FROM
PRODUCT_INFO
WHERE PRD_ID=#{prd_id}

SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT_INFO
		WHERE UPPER(PRD_NAME) LIKE UPPER('%'||#{sPrd}||'%') OR LOWER(PRD_NAME) LIKE LOWER('%'||#{sPrd}||'%')
	) A
) WHERE RN BETWEEN #{start} AND #{end}


SELECT * FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT_INFO WHERE CATE_ID=#{product.cate_id} ORDER BY PRD_ID DESC
	) A
) WHERE RN BETWEEN #{start} AND #{end}


SELECT CATE_ID, nvl(prd_img, 'resource/img/product/') AS PRD_IMG, PRD_NAME, PRD_ID, PRD_PRICE, PRD_OPT, CITY_NO FROM (
	SELECT ROWNUM AS RN, A.* FROM (
		SELECT * FROM PRODUCT_INFO ORDER BY PRD_ID DESC
	) A
) WHERE RN BETWEEN #{start} AND #{end}
